import React from 'react';
import { Link } from 'react-router-dom';
import { DeleteTaskById } from '../api';  // Updated to Task API
import { notify } from '../utils';

function TaskTable({
    tasks, pagination, fetchTasks, handleUpdateTask  // Updated to tasks
}) {
    const headers = ['Title', 'Description', 'Due Date', 'Status', 'Actions'];  // Updated headers
    const { currentPage, totalPages } = pagination;

    // Pagination functions
    const handleNextPage = () => {
        if (currentPage < totalPages) {
            handlePagination(currentPage + 1);
        }
    };

    const handlePreviousPage = () => {
        if (currentPage > 1) {
            handlePagination(currentPage - 1);
        }
    };

    const handlePagination = (currentPage) => {
        fetchTasks('', currentPage, 5);  // Fetch tasks with pagination
    };

    // Handle task deletion
    const handleDeleteTask = async (id) => {
        try {
            const { success, message } = await DeleteTaskById(id);  // Updated to Task API
            if (success) {
                notify(message, 'success');
            } else {
                notify(message, 'error');
            }
            fetchTasks();  // Re-fetch tasks after deletion
        } catch (err) {
            console.error(err);
            notify('Failed to delete Task', 'error');
        }
    };

    // Table row for tasks
    const TableRow = ({ task }) => {
        return (
            <tr>
                <td>
                    <Link to={`/task/${task._id}`} className="text-decoration-none">
                        {task.title}  {/* Display task title */}
                    </Link>
                </td>
                <td>{task.description}</td>  {/* Display task description */}
                <td>{task.dueDate}</td>  {/* Display task due date */}
                <td>{task.status}</td>  {/* Display task status */}
                <td>
                    <i
                        className='bi bi-pencil-fill text-warning me-4'
                        role="button"
                        data-bs-toggle="tooltip"
                        data-bs-placement="top"
                        title="Edit"
                        onClick={() => handleUpdateTask(task)}  // Updated to handle update task
                    ></i>
                    <i
                        className='bi bi-trash-fill text-danger'
                        role="button"
                        data-bs-toggle="tooltip"
                        data-bs-placement="top"
                        title="Delete"
                        onClick={() => handleDeleteTask(task._id)}  // Updated to handle delete task
                    ></i>
                </td>
            </tr>
        );
    };

    // Generate page numbers for pagination
    const pageNumbers = Array.from({ length: totalPages }, (_, index) => index + 1);

    return (
        <>
            <table className='table table-striped'>
                <thead>
                    <tr>
                        {
                            headers.map((header, i) => (
                                <th key={i}>{header}</th>
                            ))
                        }
                    </tr>
                </thead>
                <tbody>
                    {
                        tasks.length === 0 ? <div>Data Not Found</div>  // Updated to tasks
                            : tasks.map((task) => (  // Updated to tasks
                                <TableRow task={task} key={task._id} />  // Updated to task
                            ))
                    }
                </tbody>
            </table>

            {/* Pagination */}
            <div className="d-flex justify-content-between align-items-center my-3">
                <span className="badge bg-primary">Page {currentPage} of {totalPages}</span>
                <div>
                    <button
                        className="btn btn-outline-primary me-2"
                        onClick={handlePreviousPage}
                        disabled={currentPage === 1}
                    >
                        Previous
                    </button>
                    {pageNumbers.map(page => (
                        <button
                            key={page}
                            className={`btn btn-outline-primary me-1 ${currentPage === page ? 'active' : ''}`}
                            onClick={() => handlePagination(page)}
                        >
                            {page}
                        </button>
                    ))}
                    <button
                        className="btn btn-outline-primary ms-2"
                        onClick={handleNextPage}
                        disabled={currentPage === totalPages}
                    >
                        Next
                    </button>
                </div>
            </div>

        </>
    );
}

export default TaskTable;
