const BASE_URL = 'http://localhost:8080';

// Get all tasks with optional search, pagination (page, limit)
export const GetAllTasks = async (search = '', page = 1, limit = 5) => {
    const url = `${BASE_URL}/api/tasks?search=${search}&page=${page}&limit=${limit}`;
    const options = {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json'
        }
    };
    try {
        const result = await fetch(url, options);
        if (!result.ok) {
            throw new Error('Failed to fetch tasks');
        }
        const { data } = await result.json();
        return data;
    } catch (err) {
        console.error(err);
        return { error: 'Error fetching tasks' };
    }
};

// Get task details by task ID
