import React, { useEffect, useState } from 'react';
import TaskTable from './TaskTable';
import { GetAllTasks } from '../api';
import { notify } from '../utils';

const PendingTasks = ({ fetchTasks, pagination, handleUpdateTask }) => {
    const [pendingTasks, setPendingTasks] = useState([]);

    useEffect(() => {
        const fetchPendingTasks = async () => {
            try {
                const data = await GetAllTasks('', pagination.currentPage, 5); // Fetch 5 tasks per page
                const pending = data.tasks.filter(task => task.status === 'Pending');
                setPendingTasks(pending);
            } catch (err) {
                notify('Error fetching pending tasks', 'error');
                console.error(err);
            }
        };

        fetchPendingTasks();
    }, [pagination.currentPage]);

    return (
        <div>
            <h3>Pending Tasks</h3>
            <TaskTable
                tasks={pendingTasks}
                pagination={pagination}
                fetchTasks={fetchTasks}
                handleUpdateTask={handleUpdateTask}
            />
        </div>
    );
};

export default PendingTasks;
