import React, { useEffect, useState } from 'react';
import TaskTable from './TaskTable';  
import AddTask from './AddTask';  
import { GetAllTasks } from '../api';  // Using GetAllTasks to fetch tasks based on status
import { ToastContainer } from 'react-toastify';
import { notify } from '../utils';

const TaskManagementApp = () => {
    const [showModal, setShowModal] = useState(false);
    const [taskObj, setTaskObj] = useState(null);  

    const [pendingTasksData, setPendingTasksData] = useState({
        tasks: [],  
        pagination: {
            currentPage: 1,
            pageSize: 5,
            totalTasks: 0,  
            totalPages: 0,
        },
    });

    const [completedTasksData, setCompletedTasksData] = useState({
        tasks: [],  
        pagination: {
            currentPage: 1,
            pageSize: 5,
            totalTasks: 0,  
            totalPages: 0,
        },
    });

    const fetchTasks = async (status, page = 1, limit = 5) => {
        console.log(`Fetching ${status} tasks for page ${page}`);
        try {
            const data = await GetAllTasks('', page, limit, status);  // Fetch tasks based on status
            const pagination = {
                currentPage: page,
                pageSize: limit,
                totalTasks: data.totalTasks,
                totalPages: data.totalPages,
            };

            if (status === 'Pending') {
                setPendingTasksData({ tasks: data.tasks, pagination });
            } else if (status === 'Completed') {
                setCompletedTasksData({ tasks: data.tasks, pagination });
            }
        } catch (err) {
            notify('Error fetching tasks', 'error');
            console.error(err);
        }
    };

    useEffect(() => {
        fetchTasks('Pending', 1, 5);  // Fetch pending tasks when the component mounts
        fetchTasks('Completed', 1, 5);  // Fetch completed tasks when the component mounts
    }, []);

    const handleSearch = (e) => {
        // Search should trigger refresh for both Pending and Completed tasks
        fetchTasks('Pending', 1, 5);  
        fetchTasks('Completed', 1, 5);  
    };

    const handleUpdateTask = async (task) => {
        setTaskObj(task);
        setShowModal(true);
    };

    return (
        <div className='d-flex flex-column justify-content-center align-items-center w-100 p-3'>
            <h1>Task Management App</h1>
            <div className='w-100 d-flex justify-content-center'>
                <div className='w-80 border bg-light p-3' style={{ width: '80%' }}>
                    <div className='d-flex justify-content-between mb-3'>
                        <button className='btn btn-primary' onClick={() => setShowModal(true)}>
                            Add Task
                        </button>
                        <input
                            onChange={handleSearch}
                            type='text'
                            placeholder='Search Tasks...'
                            className='form-control w-50'
                        />
                    </div>

                    {/* Display Pending Tasks Table */}
                    <h3>Pending Tasks</h3>
                    <TaskTable
                        tasks={pendingTasksData.tasks}  
                        pagination={pendingTasksData.pagination}
                        fetchTasks={fetchTasks}  
                        handleUpdateTask={handleUpdateTask}  
                        taskType="Pending"
                    />

                    {/* Display Completed Tasks Table */}
                    <h3>Completed Tasks</h3>
                    <TaskTable
                        tasks={completedTasksData.tasks}  
                        pagination={completedTasksData.pagination}
                        fetchTasks={fetchTasks}  
                        handleUpdateTask={handleUpdateTask}  
                        taskType="Completed"
                    />

                    <AddTask
                        fetchTasks={fetchTasks}  
                        showModal={showModal}
                        setShowModal={setShowModal}
                        taskObj={taskObj}  
                    />
                </div>
            </div>
            <ToastContainer position='top-right' autoClose={3000} hideProgressBar={false} />
        </div>
    );
};

export default TaskManagementApp;
